import React from 'react';
import { Link } from 'react-router-dom';


export default function DishCard({ dish }) {
return (
<div className="card p-4 rounded-lg shadow">
<img src={dish.img} alt={dish.name} className="w-full h-40 object-cover rounded" />
<h3 className="mt-3 text-lg font-semibold">{dish.name}</h3>
<p className="text-sm">{dish.desc}</p>
<div className="mt-2 flex justify-between items-center">
<span className="font-bold">${dish.price.toFixed(2)}</span>
<Link to={`/dish/${dish.id}`} className="text-blue-600">Details</Link>
</div>
</div>
);
}
