import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import CarForm from "./components/carForm";
import CarTable from "./components/carTable";
import Menu from './components/Menu';
import Contact from './components/Contact';
import About from './components/About';
import Home from './pages/Home';

function App() {
  const [brand, setBrand] = useState("");
  const [model, setModel] = useState("");
  const [price, setPrice] = useState(""); 
  const [color, setColor] = useState("");
  const [year, setYear] = useState("");
  const [cars, setCars] = useState([]); 

  const handleAdd = () => {
    const newCar = { brand, model, year, price, color };
    // setCars([...cars, newCar]); //khodor
    const updatedcar=[...cars];
    updatedcar.push(newCar);
    setCars(updatedcar);


    //alternative for reset
    // setBrand("");
    // setModel("");
    // setYear("");
    // setPrice("");
    // setColor("");
  };

  const reset = () => {
     setBrand("");
    setModel("");
    setYear("");
    setPrice("");
    setColor("");
  };

  const handleRemove = (indexToRemove) => {
    setCars(cars.filter((_, index) => index !== indexToRemove));
  };

return (
<Router>
<Routes>
<Route path='/' element={<Home />} />
<Route path='/about' element={<About />} />
<Route path='/contact' element={<Contact />} />
  <h2>Add Car</h2>
      <CarForm
        brand={brand}
        model={model}
        year = {year}
        price={price}
        color={color}
        setBrand={setBrand}
        setModel={setModel}
        setYear = {setYear}
        setPrice={setPrice}
        setColor={setColor}
        handleAdd={handleAdd}
        reset={reset}
      />
      <CarTable cars={cars} handleRemove={handleRemove} />
</Routes>

</Router>


  );
}

export default App;
