import React from 'react';

export default function About() {
  return (
    <section className="container pad">
      <h2 className="text-3xl bold mb red">About Flames</h2>

      <p className="brown mb">
        Established in 2025, <strong>FLAMES</strong> is a modern restaurant dedicated to serving
        delicious meals made with fresh ingredients and a passion for great food.
      </p>

      <p className="brown mb">
        We offer a cozy atmosphere, friendly service, and a menu filled with dishes that bring
        comfort and flavor to your table.
      </p>

      <p className="brown">
        Whether you're here for a quick bite or a full meal, Flames is always ready to serve you.
      </p>
    </section>
  );
}
