import React from 'react';
import { useParams } from 'react-router-dom';
import { dishes } from '../data/dishes';

export default function Dish() {
  const { id } = useParams();
  const dish = dishes.find(d => d.id === parseInt(id));

  if (!dish) return <div className="p-4">Dish not found</div>;

  return (
    <div className="container mx-auto p-4">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <img src={dish.img} alt={dish.name} className="rounded" />
        <div>
          <h1 className="text-2xl font-bold">{dish.name}</h1>
          <p className="mt-2">{dish.desc}</p>
          <p className="mt-4 font-bold">Price: ${dish.price.toFixed(2)}</p>
          <button className="mt-4 px-4 py-2 rounded bg-blue-600 text-white">
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
}
