import React, { useState, useEffect } from 'react';

export default function ManageOrders() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = () => {
    fetch("http://localhost:5000/orders")
      .then(res => {
        if (!res.ok) {
          throw new Error('Failed to fetch orders');
        }
        return res.json();
      })
      .then(data => {
        console.log('Orders data:', data); // Debug
        setOrders(data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Fetch orders error:', err);
        setError(err.message);
        setLoading(false);
      });
  };

  const deleteOrder = (id) => {
    if (window.confirm('Are you sure you want to delete this order?')) {
      fetch(`http://localhost:5000/orders/${id}`, {
        method: 'DELETE'
      })
        .then(res => res.json())
        .then(() => {
          setOrders(orders.filter(o => o.id !== id));
          alert('Order deleted successfully');
        })
        .catch(err => {
          console.error(err);
          alert('Error deleting order');
        });
    }
  };

  if (loading) return <p className="container pad">Loading orders...</p>;
  if (error) return <p className="container pad red">Error: {error}</p>;

  return (
    <section className="container pad">
      <h2 className="red">Manage Orders</h2>
      <p className="brown mb">Total orders: {orders.length}</p>

      {orders.length === 0 ? (
        <p>No orders yet. Orders will appear here when customers place them.</p>
      ) : (
        <div className="table-container">
          <table className="table">
            <thead>
              <tr>
                <th>Order ID</th>
                <th>Customer</th>
                <th>Phone</th>
                <th>Total</th>
                <th>Date</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {orders.map(order => (
                <tr key={order.id}>
                  <td>{order.id}</td>
                  <td>{order.customer_name}</td>
                  <td>{order.phone || 'N/A'}</td>
                  <td>${order.total}</td>
                  <td>{new Date(order.created_at).toLocaleDateString()}</td>
                  <td>
                    <button 
                      className="btn btn-small" 
                      onClick={() => deleteOrder(order.id)}
                      style={{backgroundColor: '#ff6b6b'}}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
      
      <button onClick={fetchOrders} className="btn" style={{marginTop: '1rem'}}>
        Refresh Orders
      </button>
    </section>
  );
}
