import React, { useState } from "react";
import CarForm from "./components/carForm";
import CarTable from "./components/carTable";
import Footer from './components/Footer';
import Menu from './pages/Menu';
import Contact from './pages/Contact';
import About from './pages/About';
import Home from './pages/Home';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { MessageContext } from './pages/About'
function App() {
  const [brand, setBrand] = useState("");
  const [model, setModel] = useState("");
  const [price, setPrice] = useState("");
  const [color, setColor] = useState("");
  const [year, setYear] = useState("");
  const [cars, setCars] = useState([]); 

  const handleAdd = () => {
    const newCar = { brand, model, year, price, color };
    // setCars([...cars, newCar]); //khodor
    const updatedcar=[...cars];
    updatedcar.push(newCar);
    setCars(updatedcar);


    //alternative for reset
    // setBrand("");
    // setModel("");
    // setYear("");
    // setPrice("");
    // setColor("");
  };

  const reset = () => {
     setBrand("");
    setModel("");
    setYear("");
    setPrice("");
    setColor("");
  };

  const handleRemove = (indexToRemove) => {
    setCars(cars.filter((_, index) => index !== indexToRemove));
  };

  return (

    <div style={{ padding: "20px" }}>
      <h2>Add Car</h2>
      <CarForm
        brand={brand}
        model={model}
        year = {year}
        price={price}
        color={color}
        setBrand={setBrand}
        setModel={setModel}
        setYear = {setYear}
        setPrice={setPrice}
        setColor={setColor}
        handleAdd={handleAdd}
        reset={reset}
      />
      <CarTable cars={cars} handleRemove={handleRemove} />
    </div>
  );
}

export default App;
