import React, { useState } from 'react';

export default function Contact() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    message: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted:', formData);
    alert('Thank you! Your message has been sent.');
    setFormData({ name: '', email: '', phone: '', message: '' });
  };

  return (
    <section className="container mx-auto p-6">
      <h2 className="text-3xl font-bold mb-4 text-red-600">Contact Us</h2>

      <div className="space-y-3 text-gray-700 mb-6">
        <p><strong>Email:</strong> contact@flames54.com</p>
        <p><strong>Phone:</strong> +961 81 815 779</p>
        <p><strong>Address:</strong> Saida, Hlaliyeh Main Road</p>
      </div>

      <p className="mb-6 text-gray-600">
        Weâ€™d love to hear from you! Feel free to reach out with any questions or feedback.
      </p>

      <form
        onSubmit={handleSubmit}
        className="max-w-lg mx-auto p-6 bg-white rounded-lg shadow-md space-y-4"
      >
        <input
          type="text"
          name="name"
          placeholder="Your Name"
          value={formData.name}
          onChange={handleChange}
          className="w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-red-400"
          required
        />

        <input
          type="email"
          name="email"
          placeholder="Your Email"
          value={formData.email}
          onChange={handleChange}
          className="w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-red-400"
          required
        />

        <input
          type="tel"
          name="phone"
          placeholder="Your Phone"
          value={formData.phone}
          onChange={handleChange}
          className="w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-red-400"
        />

        <textarea
          name="message"
          placeholder="Your Message"
          value={formData.message}
          onChange={handleChange}
          className="w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-red-400 h-32"
          required
        ></textarea>

        <button
          type="submit"
          className="w-full bg-red-600 text-white py-3 rounded-md font-semibold hover:bg-red-700 transition"
        >
          Send Message
        </button>
      </form>
    </section>
  );
}
