import React, { useState, useEffect } from 'react';
import DishCard from '../components/DishCard';
import Cart from '../components/Cart';

export default function Menu() {
  const [dishes, setDishes] = useState([]);
  const [filteredDishes, setFilteredDishes] = useState([]);
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  
  // Search and filter states
  const [searchTerm, setSearchTerm] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [category, setCategory] = useState('all');
  
  // Fetch dishes from API
  useEffect(() => {
    fetch("http://localhost:5000/dishes")
      .then(res => res.json())
      .then(data => {
        setDishes(data);
        setFilteredDishes(data); // Initially show all dishes
        setLoading(false);
      })
      .catch(err => {
        console.log(err);
        setError('Failed to load dishes');
        setLoading(false);
      });
  }, []);

  // Apply filters whenever search/filter criteria change
  useEffect(() => {
    let result = dishes;
    
    // Apply search filter
    if (searchTerm) {
      result = result.filter(dish =>
        dish.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        (dish.description && dish.description.toLowerCase().includes(searchTerm.toLowerCase()))
      );
    }
    
    // Apply price filters
    if (minPrice) {
      result = result.filter(dish => dish.price >= parseFloat(minPrice));
    }
    
    if (maxPrice) {
      result = result.filter(dish => dish.price <= parseFloat(maxPrice));
    }
    
    // Apply category filter (if we had categories)
    if (category !== 'all') {
      // For now, we'll filter by first word of name as pseudo-category
      // You can add a real category field to dishes table later
      result = result.filter(dish => 
        dish.name.toLowerCase().split(' ')[0] === category
      );
    }
    
    setFilteredDishes(result);
  }, [searchTerm, minPrice, maxPrice, category, dishes]);

  const addToCart = (dish) => {
    setCartItems([...cartItems, dish]);
  };

  const removeFromCart = (index) => {
    const newCart = [...cartItems];
    newCart.splice(index, 1);
    setCartItems(newCart);
  };

  // Extract categories from dish names (temporary)
  const categories = ['all', 'pizza', 'burger', 'pasta', 'salad'];

  const clearFilters = () => {
    setSearchTerm('');
    setMinPrice('');
    setMaxPrice('');
    setCategory('all');
  };

  if (loading) return <p className="container pad">Loading menu...</p>;
  if (error) return <p className="container pad red">{error}</p>;

  return (
    <section className="container">
      <h2 className="red">Menu</h2>
      
      {/* Search and Filter Section */}
      <div className="filter-section" style={{
        backgroundColor: '#fff3e6',
        padding: '1.5rem',
        borderRadius: '8px',
        marginBottom: '2rem',
        border: '1px solid #b33a2f'
      }}>
        <h3 className="brown mb">Search & Filter</h3>
        
        <div className="filter-grid" style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
          gap: '1rem',
          marginBottom: '1rem'
        }}>
          {/* Search by name/description */}
          <div className="filter-group">
            <label className="brown">Search Dishes:</label>
            <input
              type="text"
              placeholder="Search by name or description..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="form-input"
              style={{ width: '100%' }}
            />
          </div>
          
          {/* Price Range */}
          <div className="filter-group">
            <label className="brown">Price Range:</label>
            <div style={{ display: 'flex', gap: '0.5rem' }}>
              <input
                type="number"
                placeholder="Min $"
                value={minPrice}
                onChange={(e) => setMinPrice(e.target.value)}
                className="form-input"
                min="0"
                step="0.01"
              />
              <span style={{ alignSelf: 'center' }}>to</span>
              <input
                type="number"
                placeholder="Max $"
                value={maxPrice}
                onChange={(e) => setMaxPrice(e.target.value)}
                className="form-input"
                min="0"
                step="0.01"
              />
            </div>
          </div>
          
          {/* Category Filter */}
          <div className="filter-group">
            <label className="brown">Category:</label>
            <select
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              className="form-input"
              style={{ width: '100%' }}
            >
              {categories.map(cat => (
                <option key={cat} value={cat}>
                  {cat === 'all' ? 'All Categories' : cat.charAt(0).toUpperCase() + cat.slice(1)}
                </option>
              ))}
            </select>
          </div>
        </div>
        
        {/* Results and Clear button */}
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginTop: '1rem'
        }}>
          <p className="brown">
            Showing {filteredDishes.length} of {dishes.length} dishes
          </p>
          <button
            onClick={clearFilters}
            className="btn"
            style={{ backgroundColor: '#666' }}
          >
            Clear Filters
          </button>
        </div>
      </div>

      {/* Dishes Grid */}
      {filteredDishes.length === 0 ? (
        <div className="no-results" style={{
          textAlign: 'center',
          padding: '2rem',
          backgroundColor: '#fff3e6',
          borderRadius: '8px',
          border: '1px dashed #b33a2f'
        }}>
          <h3 className="red">No dishes found</h3>
          <p className="brown">Try adjusting your search or filters</p>
          <button
            onClick={clearFilters}
            className="btn"
            style={{ marginTop: '1rem' }}
          >
            Show All Dishes
          </button>
        </div>
      ) : (
        <div className="grid">
          {filteredDishes.map(d => (
            <DishCard
              key={d.id}
              dish={d}
              addToCart={() => addToCart(d)}
            />
          ))}
        </div>
      )}

      <Cart cartItems={cartItems} removeFromCart={removeFromCart} />
    </section>
  );
}
