import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function AddDish() {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [description, setDescription] = useState('');
  const [image, setImage] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const addDish = () => {
    if (!name || !price || !description) {
      setMessage('Please fill all fields');
      return;
    }

    axios.post("http://localhost:5000/dishes", { 
      name, 
      price: parseFloat(price), 
      description, 
      image: image || '/assets/default.jpg' 
    })
      .then(() => {
        setMessage('Dish added successfully!');
        // Clear form
        setName('');
        setPrice('');
        setDescription('');
        setImage('');
        // Redirect after 2 seconds
        setTimeout(() => navigate('/menu'), 2000);
      })
      .catch(err => {
        console.log(err);
        setMessage('Error adding dish');
      });
  };

  return (
    <section className="container pad">
      <h2 className="red">Add New Dish</h2>
      
      {message && <p className={message.includes('success') ? 'green' : 'red'}>{message}</p>}
      
      <div className="form-group">
        <label>Dish Name:</label>
        <input 
          type="text" 
          value={name}
          onChange={e => setName(e.target.value)}
          placeholder="e.g., French Fries"
          className="form-input"
        />
      </div>

      <div className="form-group">
        <label>Price ($):</label>
        <input 
          type="number" 
          value={price}
          onChange={e => setPrice(e.target.value)}
          placeholder="e.g., 5.99"
          step="0.01"
          className="form-input"
        />
      </div>

      <div className="form-group">
        <label>Description:</label>
        <textarea 
          value={description}
          onChange={e => setDescription(e.target.value)}
          placeholder="Describe the dish..."
          className="form-input"
          rows="3"
        />
      </div>

      <div className="form-group">
        <label>Image URL (optional):</label>
        <input 
          type="text" 
          value={image}
          onChange={e => setImage(e.target.value)}
          placeholder="/assets/fries.jpg"
          className="form-input"
        />
      </div>

      <button onClick={addDish} className="btn">Add Dish</button>
    </section>
  );
}
